INPUT = ''''''
INPUT_SAMPLE = [
    ('mjqjpqmgbljsphdztnvjfqwrcgsmlb', 7, 19),
    ('bvwbjplbgvbhsrlpgdmjqwftvncz', 5, 23),
    ('nppdvjthqldpwncqszvftbrmjlhg', 6, 23),
    ('nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg', 10, 29),
    ('zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw', 11, 26),
]

PART_ONE_DISTINCT_CHARS = 4
PART_TWO_DISTINCT_CHARS = 14

def dec06(input, distinct_chars):
    hasDuplicate = lambda x: len(x) != len(set([*x]))

    for i in range(distinct_chars, len(input)):
        if not (hasDuplicate(input[i - distinct_chars : i])):
            return i
    raise

for input, expected_part_one, expected_part_two in INPUT_SAMPLE:
    assert dec06(input, PART_ONE_DISTINCT_CHARS) == expected_part_one, f'{dec06(input, PART_ONE_DISTINCT_CHARS)} != {expected_part_one}'
    assert dec06(input, PART_TWO_DISTINCT_CHARS) == expected_part_two, f'{dec06(input, PART_TWO_DISTINCT_CHARS)} != {expected_part_two}'

def main():
    print(f'Part One: {dec06(INPUT, PART_ONE_DISTINCT_CHARS)}')
    print(f'Part Two: {dec06(INPUT, PART_TWO_DISTINCT_CHARS)}')

main()
